import React from "react"
import data from './list.json'
import { Card, Typography } from "@material-tailwind/react";
export const Appointment =() =>{
    
    const appointments = data.appointments;
    const TABLE_HEAD = ["Patient", "Date", "Time", "Doctor","Injury","Action"];
    const formatDate = (dateString) => {

      const options = {day:"2-digit",month:"short",year:"numeric"}
      const date = new Date(dateString)
      return date.toLocaleDateString("en-IN",options).replace(/-/g, ' ')
    };

    const formatTime = (timeString) => {

     return timeString.toLocalTimeString.replace(/AM/g,'')
      
    }

    return(
        <div>
            <h1> Appointment list</h1>
    <Card className="h-full w-full overflow-scroll">
      <table className="w-full text-sm text-left text-gray-500 dark:text-gray-400">
 
        <thead>
          <tr className="border-customRed-500">
            {TABLE_HEAD.map((head) => (
              <th key={head} className="border-b border-blue-gray-100 bg-blue-gray-50 p-4">
                <Typography
                  variant="small"
                  color="blue-gray"
                  className="font-normal leading-none opacity-70"
                >
                  {head}
                </Typography>
              </th>
            ))}
          </tr>
        </thead>
        <tbody>
          {appointments.map((appointments) => (
          <tr key={appointments.patient_name} className="border-b border-red-500">
                <td className="p-4">
                <Typography variant="small" color="blue-gray" className="font-normal">
                  <div className="flex flex-col">
                 <span >{appointments.patient_name}</span> 
                  <spam>{appointments.mobile_number}</spam>
                  </div>
               
                </Typography>
              </td>
              <td className="p-4">
             
                <Typography variant="small" color="blue-gray" className="font-bold p-2 text-left">
                {formatDate(appointments.appointment_date)}
                </Typography>
              </td>
              <td className="p-4">
                <Typography variant="small" color="blue-gray" className="font-normal">
                  {formatTime(appointments.appointment_time)}
                </Typography>
              </td>
              <td className="p-4">
              <Typography variant="small" color="blue-gray" className="font-normal">
                {appointments.doctor}
                </Typography>
              </td>
              <td className="p-4">
              <Typography variant="small" color="blue-gray" className="font-normal">
                {appointments.injury}
                </Typography>
              </td>
              <td className="p-4">
              <Typography variant="small" color="blue-gray" className="font-medium">
        <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor" class="w-5 h-5">
  <path d="M10 3a1.5 1.5 0 1 1 0 3 1.5 1.5 0 0 1 0-3ZM10 8.5a1.5 1.5 0 1 1 0 3 1.5 1.5 0 0 1 0-3ZM11.5 15.5a1.5 1.5 0 1 0-3 0 1.5 1.5 0 0 0 3 0Z" />
</svg>
                </Typography>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </Card>
</div>
    );
    
}
